import React, { Component } from "react";
import Aux from "../../hoc/aux";
import Navigation from "../../components/Navigation/Navigation";
import "./shrink.css";
import axios from "axios";

class Shrink extends Component {
constructor(props){
  super(props)
  this.state = {
    mainurl: "",
    shorturl: "",
    success: false,
  };
  this.handleInput = this.handleInput.bind(this);
  this.handleSubmit = this.handleSubmit.bind(this);
}

  state = {
    mainurl: "",
    shorturl: "",
    success: false,
  };

  handleInput(event){
    this.setState({mainurl: event.target.value})
    console.log(this.state.mainurl);
  }

  async handleSubmit(event){
    const request = await axios.post("localhost:5000/post/", this.state.mainurl)
    console.log(request.data)
    event.preventDefault()
    console.log(this.state);
  }

  render() {
    return (
      <Aux>
        <Navigation />
        <main>
          <div className="w-50 mx-auto rounded-4 shadow p-5 mt-5">
            <form className="" action="" method="POST">
              <div>
                <label htmlFor="mainurl" className="ml125">
                  Enter the URL Here
                </label>
                <input
                  className="form-control rounded-2 w-75 mx-auto"
                  type="url"
                  placeholder="https://website.com/abcdefghi"
                  value={this.state.value}
                  onChange={this.handleInput}
                  name="mainurl"
                  id="mainurl"
                />
              <br></br>
                <input
                  type="submit"
                  id="submit"
                  className="btn btn-primary mr125"
                  value="Submit"
                  onClick={this.handleSubmit}
                />
              </div>
            </form>
          </div>
        </main>
      </Aux>
    );
  }
}

export default Shrink;
